CREATE TABLE "doctor_doctor_review" ("review_id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NULL, "message" varchar(1000) NULL, "doctor_id" integer NULL REFERENCES "doctor_doctor_information" ("doctor_id") DEFERRABLE INITIALLY DEFERRED, "patient_id" integer NULL REFERENCES "hospital_patient" ("patient_id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "doctor_education" ("education_id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "degree" varchar(200) NULL, "institute" varchar(200) NULL, "year_of_completion" varchar(200) NULL, "doctor_id" integer NULL REFERENCES "doctor_doctor_information" ("doctor_id") DEFERRABLE INITIALLY DEFERRED);
